(function(w){function C(ctx,cfg){this.ctx=ctx;this.cfg=cfg;this.draw()}C.prototype.draw=function(){var c=this.ctx.canvas,x=this.ctx,d=this.cfg.data,l=d.labels||[],s=this.cfg.data.datasets||[];x.clearRect(0,0,c.width,c.height);var p=36,w=c.width,h=c.height;x.strokeStyle='#d5e3ef';for(var i=0;i<5;i++){var yy=p+i*(h-2*p)/4;x.beginPath();x.moveTo(p,yy);x.lineTo(w-p,yy);x.stroke()}s.forEach(function(ds,idx){var vals=(ds.data||[]).filter(function(v){return v!==null});if(!vals.length)return;var min=Math.min.apply(null,vals),max=Math.max.apply(null,vals);if(min===max){min-=1;max+=1}x.strokeStyle=ds.borderColor||['#0f6cbf','#0f9d58','#d04f2b'][idx%3];x.lineWidth=2;x.beginPath();for(var j=0;j<ds.data.length;j++){var v=ds.data[j];if(v===null)continue;var xx=p+j*((w-2*p)/Math.max(1,l.length-1));var yy=(h-p)-((v-min)*(h-2*p)/(max-min));if(j===0)x.moveTo(xx,yy);else x.lineTo(xx,yy)}x.stroke();});};w.Chart=C;})(window);
